/**
 * Adapter with delegation pattern
 */
private class DelegationAdapter extends AbsDelegationAdapter<List<?>> {

    DelegationAdapter(Context context) {
        delegatesManager.addDelegate(new Delegate(context));
        delegatesManager.setFallbackDelegate(new DefaultDelegate(context));
    }
}

